mapper.select:
SELECT idx, seller, customer, goodsIdx, sellerDel, customerDel, regDate
FROM tbl_chat_group
WHERE seller=#{memberId} OR customer=#{memberId}

SELECT idx, groupIdx, senderId, content, regDate, sellerDel, customerDel
FROM tbl_chat_message
WHERE groupIdx = #{groupIdx}

SELECT IFNULL(groupIdx,-1)
FROM tbl_chat_group
WHERE goodsIdx = #{goodsIdx} AND customer = #{customer}
