mapper.select:
SELECT idx, buyer, seller, content, score, `status`, regDate, modifyDate, deleteDate
FROM tbl_review
WHERE idx=#{idx}

SELECT idx, buyer, seller, content, score, `status`, regDate, modifyDate, deleteDate
FROM tbl_review

${searchCategory} LIKE CONCAT('%',#{searchKeyword},'%')


    ORDER BY regDate desc


    ORDER BY ${orderBy} ${orderDir}

LIMIT #{page_skip_count}, #{page_size}

mapper.update:
UPDATE tbl_review
buyer=#{buyer}, seller=#{seller}, content=#{content}, score=#{score}, modifyDate = NOW()
WHERE idx=#{idx}

UPDATE tbl_review
SET `status` = 'N', deleteDate = NOW()
WHERE idx=#{idx}
